<?php require_once(PLUGIN_PATH."/multi_language/classes/LanguageProvider.php"); ?><td data-title="<?php echo __('note',$currentvalue) ?>"><?php $value =  $currentitem[$currentvalue['name']]; ?><?php $langs = \MultiLanguage\Classes\LanguageProvider::instance()->getLanguages();$flags = \MultiLanguage\Classes\LanguageProvider::instance()->flags();?><?php foreach ($langs as $k => $lang):?>	<?php 	$addStyle = $lang==$value?'border: 4px solid #157c18;':'';	 $flag = array_key_exists($lang, $flags)? sprintf('<img style="border: 1px solid #c0c0c0;height: 18px;%s" src="%s"/>',$addStyle,$flags[$lang]['flag']):$lang; ?>	<a data-lang="<?php echo $lang ?>" class="<?php echo $lang==$value?'active':'' ?>" href="Techsystem/extra?action=<?php echo base64_encode("table=".$table[0]['name']."&action=view&code=multi_language_edit")?>&table=<?php echo $table[0]['name'] ?>&id=<?php echo $currentitem['id'] ?>&lang=<?php echo $lang ?>&return=<?php echo base64_encode(current_full_url()) ?>"><?php echo $flag ?></a><?php endforeach ?></td>