<?phpnamespace SyncDataCms\Auth;use SyncDataCms\Contracts\BaseConnect;use SyncDataCms\Models\Account;class AccessToken extends BaseConnect{    const URL_REQUEST_TOKEN = 'https://id.kiotviet.vn/connect/token';    protected $headers = [];    public function __construct(Account $account){        parent::__construct($account,static::URL_REQUEST_TOKEN);    }        public function execute(){        $result =  $this->_execute([            'scopes' => 'PublicApi.Access',            'grant_type' => 'client_credentials',            'client_id' => $this->account->client_id,            'client_secret' => $this->account->client_secret        ]);        $json = json_decode($result,true);        $accessToken = array_key_exists('access_token',$json)?$json['access_token']:'';        return $accessToken;    }}